#include <stdio.h>
#include <stdlib.h>
#include <time.h>
#include <omp.h>
#define N 5

int main() {
     /* DECLARACION DE VARIABLES */
    float A[N][N], B[N][N], C[N][N];
    int i, j, k;
    /* LLENAR LAS MATRICES CON NUMEROS ALEATORIOS */
    srand(time(NULL));
    for (i = 0; i < N; i++) {
        for (j = 0; j < N; j++) {
            A[i][j] = rand() % 10;
            B[i][j] = rand() % 10;
        }
    }
    /* MULTIPLICACION DE LAS MATRICES */
    #pragma omp parallel for private(j, k)
    for (i = 0; i < N; i++) {
        for (j = 0; j < N; j++) {
            C[i][j] = 0;
            for (k = 0; k < N; k++) {
                C[i][j] += A[i][k] * B[k][j];
            }
        }
    }
    for (i = 0; i < N; i++) {
        for (j = 0; j < N; j++) {
            printf("%f ", C[i][j]);
        }
        printf("\n");
    }
    /* FINALIZAR EL PROGRAMA */
    return 0;
}
